params.query:
-- Create profiles table
create table profiles (
  id uuid primary key references auth.users on delete cascade,
  email text,
  is_admin boolean default false,
  is_subscribed boolean default false,
  subscription_end_date timestamptz,
  created_at timestamptz default now(),
  updated_at timestamptz default now()
);

-- Enable RLS
alter table profiles enable row level security;

-- Create RLS policies
create policy "Users can view their own profile"
  on profiles
  for select
  to authenticated
  using ( (select auth.uid()) = id );

create policy "Users can update their own profile"
  on profiles
  for update
  to authenticated
  using ( (select auth.uid()) = id );

create policy "Admins can view all profiles"
  on profiles
  for select
  to authenticated
  using ( 
    exists (
      select 1 from profiles
      where id = (select auth.uid()) and is_admin = true
    )
  );

create policy "Admins can update all profiles"
  on profiles
  for update
  to authenticated
  using ( 
    exists (
      select 1 from profiles
      where id = (select auth.uid()) and is_admin = true
    )
  );

-- Create index for performance
create index profiles_user_id_idx on profiles(id);
create index profiles_is_admin_idx on profiles(is_admin);

-- Create function to handle new user creation
create or replace function handle_new_user()
returns trigger as $$
begin
  insert into public.profiles (id, email)
  values (new.id, new.email);
  return new;
end;
$$ language plpgsql security definer;

-- Create trigger to automatically create profile on signup
create trigger on_auth_user_created
  after insert on auth.users
  for each row execute function handle_new_user();

-- Create videos table
create table videos (
  id uuid primary key default gen_random_uuid(),
  title text not null,
  description text,
  video_url text not null,
  thumbnail_url text,
  duration text,
  uploaded_by uuid references auth.users on delete set null,
  created_at timestamptz default now(),
  updated_at timestamptz default now()
);

-- Enable RLS
alter table videos enable row level security;

-- Create RLS policies
create policy "Subscribed users can view videos"
  on videos
  for select
  to authenticated
  using ( 
    exists (
      select 1 from profiles
      where id = (select auth.uid()) 
      and is_subscribed = true
      and (subscription_end_date is null or subscription_end_date > now())
    )
  );

create policy "Admins can insert videos"
  on videos
  for insert
  to authenticated
  with check ( 
    exists (
      select 1 from profiles
      where id = (select auth.uid()) and is_admin = true
    )
  );

create policy "Admins can update videos"
  on videos
  for update
  to authenticated
  using ( 
    exists (
      select 1 from profiles
      where id = (select auth.uid()) and is_admin = true
    )
  );

create policy "Admins can delete videos"
  on videos
  for delete
  to authenticated
  using ( 
    exists (
      select 1 from profiles
      where id = (select auth.uid()) and is_admin = true
    )
  );

-- Create indexes for performance
create index videos_created_at_idx on videos(created_at desc);
create index videos_uploaded_by_idx on videos(uploaded_by);

-- Create surf_reports table
create table surf_reports (
  id uuid primary key default gen_random_uuid(),
  date date not null unique,
  wave_height text not null,
  wind_speed text not null,
  wind_direction text not null,
  tide text not null,
  water_temp text not null,
  conditions text not null,
  rating integer check (rating >= 0 and rating <= 10),
  created_by uuid references auth.users on delete set null,
  created_at timestamptz default now(),
  updated_at timestamptz default now()
);

-- Enable RLS
alter table surf_reports enable row level security;

-- Create RLS policies
create policy "Subscribed users can view surf reports"
  on surf_reports
  for select
  to authenticated
  using ( 
    exists (
      select 1 from profiles
      where id = (select auth.uid()) 
      and is_subscribed = true
      and (subscription_end_date is null or subscription_end_date > now())
    )
  );

create policy "Admins can insert surf reports"
  on surf_reports
  for insert
  to authenticated
  with check ( 
    exists (
      select 1 from profiles
      where id = (select auth.uid()) and is_admin = true
    )
  );

create policy "Admins can update surf reports"
  on surf_reports
  for update
  to authenticated
  using ( 
    exists (
      select 1 from profiles
      where id = (select auth.uid()) and is_admin = true
    )
  );

create policy "Admins can delete surf reports"
  on surf_reports
  for delete
  to authenticated
  using ( 
    exists (
      select 1 from profiles
      where id = (select auth.uid()) and is_admin = true
    )
  );

-- Create indexes for performance
create index surf_reports_date_idx on surf_reports(date desc);
create index surf_reports_created_by_idx on surf_reports(created_by);

-- Storage policies for videos bucket
create policy "Subscribed users can view videos"
  on storage.objects
  for select
  to authenticated
  using ( 
    bucket_id = 'videos' and
    exists (
      select 1 from profiles
      where id = (select auth.uid()) 
      and is_subscribed = true
      and (subscription_end_date is null or subscription_end_date > now())
    )
  );

create policy "Admins can upload videos"
  on storage.objects
  for insert
  to authenticated
  with check ( 
    bucket_id = 'videos' and
    exists (
      select 1 from profiles
      where id = (select auth.uid()) and is_admin = true
    )
  );

create policy "Admins can update videos"
  on storage.objects
  for update
  to authenticated
  using ( 
    bucket_id = 'videos' and
    exists (
      select 1 from profiles
      where id = (select auth.uid()) and is_admin = true
    )
  );

create policy "Admins can delete videos"
  on storage.objects
  for delete
  to authenticated
  using ( 
    bucket_id = 'videos' and
    exists (
      select 1 from profiles
      where id = (select auth.uid()) and is_admin = true
    )
  );

