import { serve } from 'https://deno.land/std@0.168.0/http/server.ts';
import { createClient } from 'https://esm.sh/@supabase/supabase-js@2.87.3';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

serve(async (req) => {
  // Handle CORS preflight requests
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    // Create a Supabase client with the service role key
    const supabaseAdmin = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_SERVICE_ROLE_KEY') ?? '',
      {
        auth: {
          autoRefreshToken: false,
          persistSession: false
        }
      }
    );

    const adminEmail = 'lydonmn@gmail.com';
    const adminPassword = 'Federalreserve69$';

    console.log('Creating admin user:', adminEmail);

    // Check if user already exists
    const { data: existingUsers, error: listError } = await supabaseAdmin.auth.admin.listUsers();

    if (listError) {
      console.error('Error listing users:', listError);
      throw listError;
    }

    const existingUser = existingUsers.users.find(u => u.email === adminEmail);

    let userId: string;

    if (existingUser) {
      console.log('User already exists, updating...');
      userId = existingUser.id;

      // Update password
      const { error: updateError } = await supabaseAdmin.auth.admin.updateUserById(
        userId,
        { password: adminPassword }
      );

      if (updateError) {
        console.error('Error updating user:', updateError);
        throw updateError;
      }
    } else {
      console.log('Creating new user...');

      // Create the admin user with email confirmation bypassed
      const { data: userData, error: createError } = await supabaseAdmin.auth.admin.createUser({
        email: adminEmail,
        password: adminPassword,
        email_confirm: true, // Auto-confirm email
      });

      if (createError) {
        console.error('Error creating user:', createError);
        throw createError;
      }

      if (!userData.user) {
        throw new Error('User creation failed - no user data returned');
      }

      userId = userData.user.id;
      console.log('User created with ID:', userId);
    }

    // Check if profile exists
    const { data: existingProfile, error: profileCheckError } = await supabaseAdmin
      .from('profiles')
      .select('*')
      .eq('id', userId)
      .single();

    if (profileCheckError && profileCheckError.code !== 'PGRST116') {
      console.error('Error checking profile:', profileCheckError);
      throw profileCheckError;
    }

    if (existingProfile) {
      console.log('Profile exists, updating to admin...');

      // Update existing profile to be admin
      const { error: updateProfileError } = await supabaseAdmin
        .from('profiles')
        .update({
          email: adminEmail,
          is_admin: true,
          updated_at: new Date().toISOString()
        })
        .eq('id', userId);

      if (updateProfileError) {
        console.error('Error updating profile:', updateProfileError);
        throw updateProfileError;
      }
    } else {
      console.log('Creating admin profile...');

      // Create profile with admin privileges
      const { error: insertProfileError } = await supabaseAdmin
        .from('profiles')
        .insert({
          id: userId,
          email: adminEmail,
          is_admin: true,
          is_subscribed: false,
          created_at: new Date().toISOString(),
          updated_at: new Date().toISOString()
        });

      if (insertProfileError) {
        console.error('Error creating profile:', insertProfileError);
        throw insertProfileError;
      }
    }

    console.log('Admin user setup complete!');

    return new Response(
      JSON.stringify({
        success: true,
        message: 'Admin user created/updated successfully',
        email: adminEmail,
        userId: userId
      }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 200,
      }
    );
  } catch (error) {
    console.error('Error in create-admin function:', error);
    return new Response(
      JSON.stringify({
        success: false,
        error: error.message
      }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 400,
      }
    );
  }
});

