-- Drop existing policies for videos
DROP POLICY IF EXISTS "Subscribed users can view videos" ON videos;

-- Create new policy that allows admins OR subscribed users to view videos
CREATE POLICY "Admins and subscribed users can view videos" ON videos
FOR SELECT
TO authenticated
USING (
  EXISTS (
    SELECT 1 FROM profiles
    WHERE profiles.id = auth.uid()
    AND (
      profiles.is_admin = true
      OR (
        profiles.is_subscribed = true
        AND (profiles.subscription_end_date IS NULL OR profiles.subscription_end_date > now())
      )
    )
  )
);

-- Drop existing policies for surf_reports
DROP POLICY IF EXISTS "Subscribed users can view surf reports" ON surf_reports;

-- Create new policy that allows admins OR subscribed users to view surf reports
CREATE POLICY "Admins and subscribed users can view surf reports" ON surf_reports
FOR SELECT
TO authenticated
USING (
  EXISTS (
    SELECT 1 FROM profiles
    WHERE profiles.id = auth.uid()
    AND (
      profiles.is_admin = true
      OR (
        profiles.is_subscribed = true
        AND (profiles.subscription_end_date IS NULL OR profiles.subscription_end_date > now())
      )
    )
  )
);

-- Drop existing policies that have circular dependencies
DROP POLICY IF EXISTS "Users can view their own profile" ON profiles;
DROP POLICY IF EXISTS "Users can update their own profile" ON profiles;
DROP POLICY IF EXISTS "Admins can view all profiles" ON profiles;
DROP POLICY IF EXISTS "Admins can update all profiles" ON profiles;

-- Create simple, non-circular policies
-- Allow users to view their own profile
CREATE POLICY "Users can view their own profile"
ON profiles FOR SELECT
TO authenticated
USING (auth.uid() = id);

-- Allow users to update their own profile
CREATE POLICY "Users can update their own profile"
ON profiles FOR UPDATE
TO authenticated
USING (auth.uid() = id);

-- Allow users to insert their own profile (needed for auto-creation)
CREATE POLICY "Users can insert their own profile"
ON profiles FOR INSERT
TO authenticated
WITH CHECK (auth.uid() = id);

-- Service role can do everything (for admin operations via Edge Functions)
-- No need for separate admin policies since service role bypasses RLS

