import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

// Folly Beach coordinates
const FOLLY_BEACH_LAT = 32.6552;
const FOLLY_BEACH_LON = -79.9403;

// NOAA API endpoints
const NOAA_POINTS_API = `https://api.weather.gov/points/${FOLLY_BEACH_LAT},${FOLLY_BEACH_LON}`;

interface WeatherData {
  date: string;
  temperature: number;
  feels_like: number;
  humidity: number;
  wind_speed: number;
  wind_direction: string;
  wind_gust: number;
  pressure: number;
  visibility: number;
  conditions: string;
  forecast: string;
  raw_data: any;
}

Deno.serve(async (req: Request) => {
  try {
    // Create Supabase client
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    console.log('Fetching NOAA weather data for Folly Beach...');

    // Step 1: Get the forecast office and grid coordinates
    const pointsResponse = await fetch(NOAA_POINTS_API, {
      headers: {
        'User-Agent': 'SurfVista App (contact@surfvista.com)',
        'Accept': 'application/json'
      }
    });

    if (!pointsResponse.ok) {
      throw new Error(`NOAA Points API error: ${pointsResponse.status}`);
    }

    const pointsData = await pointsResponse.json();
    const forecastUrl = pointsData.properties.forecast;
    const observationStationsUrl = pointsData.properties.observationStations;

    // Step 2: Get the forecast
    const forecastResponse = await fetch(forecastUrl, {
      headers: {
        'User-Agent': 'SurfVista App (contact@surfvista.com)',
        'Accept': 'application/json'
      }
    });

    if (!forecastResponse.ok) {
      throw new Error(`NOAA Forecast API error: ${forecastResponse.status}`);
    }

    const forecastData = await forecastResponse.json();
    const todayForecast = forecastData.properties.periods[0];

    // Step 3: Get observation stations
    const stationsResponse = await fetch(observationStationsUrl, {
      headers: {
        'User-Agent': 'SurfVista App (contact@surfvista.com)',
        'Accept': 'application/json'
      }
    });

    if (!stationsResponse.ok) {
      throw new Error(`NOAA Stations API error: ${stationsResponse.status}`);
    }

    const stationsData = await stationsResponse.json();
    const nearestStation = stationsData.features[0]?.id;

    // Step 4: Get current observations
    let currentObservation = null;
    if (nearestStation) {
      const observationUrl = `https://api.weather.gov/stations/${nearestStation}/observations/latest`;
      const observationResponse = await fetch(observationUrl, {
        headers: {
          'User-Agent': 'SurfVista App (contact@surfvista.com)',
          'Accept': 'application/json'
        }
      });

      if (observationResponse.ok) {
        currentObservation = await observationResponse.json();
      }
    }

    // Extract weather data
    const obs = currentObservation?.properties;
    const weatherData: WeatherData = {
      date: new Date().toISOString().split('T')[0],
      temperature: obs?.temperature?.value ? Math.round((obs.temperature.value * 9/5) + 32) : todayForecast.temperature,
      feels_like: obs?.heatIndex?.value ? Math.round((obs.heatIndex.value * 9/5) + 32) : todayForecast.temperature,
      humidity: obs?.relativeHumidity?.value || 0,
      wind_speed: obs?.windSpeed?.value ? Math.round(obs.windSpeed.value * 0.621371) : parseInt(todayForecast.windSpeed) || 0,
      wind_direction: obs?.windDirection?.value ? getWindDirection(obs.windDirection.value) : todayForecast.windDirection || 'N',
      wind_gust: obs?.windGust?.value ? Math.round(obs.windGust.value * 0.621371) : 0,
      pressure: obs?.barometricPressure?.value ? Math.round(obs.barometricPressure.value / 100) : 0,
      visibility: obs?.visibility?.value ? Math.round(obs.visibility.value / 1609.34) : 10,
      conditions: todayForecast.shortForecast || 'Unknown',
      forecast: todayForecast.detailedForecast || '',
      raw_data: {
        observation: obs,
        forecast: todayForecast
      }
    };

    // Store in database
    const { data, error } = await supabase
      .from('weather_data')
      .upsert(weatherData, {
        onConflict: 'date',
        ignoreDuplicates: false
      })
      .select()
      .single();

    if (error) {
      console.error('Error storing weather data:', error);
      throw error;
    }

    console.log('Weather data stored successfully:', data);

    return new Response(
      JSON.stringify({ success: true, data }),
      {
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  } catch (error) {
    console.error('Error fetching weather data:', error);
    return new Response(
      JSON.stringify({ success: false, error: error.message }),
      {
        status: 500,
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  }
});

function getWindDirection(degrees: number): string {
  const directions = ['N', 'NNE', 'NE', 'ENE', 'E', 'ESE', 'SE', 'SSE', 'S', 'SSW', 'SW', 'WSW', 'W', 'WNW', 'NW', 'NNW'];
  const index = Math.round(degrees / 22.5) % 16;
  return directions[index];
}

import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

// NOAA Tides & Currents API
// Station ID for Folly Beach (Charleston Harbor)
const STATION_ID = '8665530'; // Charleston, Cooper River Entrance
const NOAA_TIDES_API = 'https://api.tidesandcurrents.noaa.gov/api/prod/datagetter';

interface TideData {
  date: string;
  time: string;
  type: 'high' | 'low';
  height: number;
}

Deno.serve(async (req: Request) => {
  try {
    // Create Supabase client
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    console.log('Fetching tide data for Folly Beach...');

    // Get today's date and tomorrow's date
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);

    const beginDate = today.toISOString().split('T')[0].replace(/-/g, '');
    const endDate = tomorrow.toISOString().split('T')[0].replace(/-/g, '');

    // Fetch tide predictions from NOAA
    const tideUrl = `${NOAA_TIDES_API}?product=predictions&application=SurfVista&begin_date=${beginDate}&end_date=${endDate}&datum=MLLW&station=${STATION_ID}&time_zone=lst_ldt&units=english&interval=hilo&format=json`;

    const tideResponse = await fetch(tideUrl, {
      headers: {
        'User-Agent': 'SurfVista App (contact@surfvista.com)',
        'Accept': 'application/json'
      }
    });

    if (!tideResponse.ok) {
      throw new Error(`NOAA Tides API error: ${tideResponse.status}`);
    }

    const tideData = await tideResponse.json();

    if (!tideData.predictions || tideData.predictions.length === 0) {
      throw new Error('No tide predictions available');
    }

    // Process tide data
    const tideRecords: TideData[] = tideData.predictions.map((prediction: any) => {
      const dateTime = new Date(prediction.t);
      return {
        date: dateTime.toISOString().split('T')[0],
        time: dateTime.toTimeString().split(' ')[0],
        type: prediction.type.toLowerCase() as 'high' | 'low',
        height: parseFloat(prediction.v)
      };
    });

    // Store in database
    const { data, error } = await supabase
      .from('tide_data')
      .upsert(tideRecords, {
        onConflict: 'date,time,type',
        ignoreDuplicates: false
      })
      .select();

    if (error) {
      console.error('Error storing tide data:', error);
      throw error;
    }

    console.log(`Stored ${data.length} tide records successfully`);

    return new Response(
      JSON.stringify({ success: true, count: data.length, data }),
      {
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  } catch (error) {
    console.error('Error fetching tide data:', error);
    return new Response(
      JSON.stringify({ success: false, error: error.message }),
      {
        status: 500,
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  }
});

import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

// NOAA Buoy Data - Buoy 41004 (Edisto, SC - closest to Folly Beach)
const BUOY_ID = '41004';
const NOAA_BUOY_API = `https://www.ndbc.noaa.gov/data/realtime2/${BUOY_ID}.txt`;

interface SurfReportData {
  date: string;
  source: string;
  wave_height: string;
  wave_period: string;
  swell_direction: string;
  conditions: string;
  rating: number;
  raw_data: any;
}

Deno.serve(async (req: Request) => {
  try {
    // Create Supabase client
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    console.log('Fetching surf report data from NOAA buoy...');

    // Fetch buoy data
    const buoyResponse = await fetch(NOAA_BUOY_API, {
      headers: {
        'User-Agent': 'SurfVista App (contact@surfvista.com)'
      }
    });

    if (!buoyResponse.ok) {
      throw new Error(`NOAA Buoy API error: ${buoyResponse.status}`);
    }

    const buoyText = await buoyResponse.text();
    const lines = buoyText.split('\n');

    // Parse the latest data (skip header lines)
    if (lines.length < 3) {
      throw new Error('Insufficient buoy data');
    }

    // Get the latest reading (line 2, after headers)
    const latestData = lines[2].trim().split(/\s+/);

    // Parse buoy data format:
    // YY MM DD hh mm WDIR WSPD GST WVHT DPD APD MWD PRES ATMP WTMP DEWP VIS TIDE
    const waveHeight = parseFloat(latestData[8]); // WVHT - significant wave height in meters
    const dominantPeriod = parseFloat(latestData[9]); // DPD - dominant wave period in seconds
    const waveDirection = parseInt(latestData[11]); // MWD - mean wave direction
    const windSpeed = parseFloat(latestData[6]); // WSPD - wind speed in m/s

    // Convert to imperial units
    const waveHeightFt = (waveHeight * 3.28084).toFixed(1);
    const windSpeedMph = (windSpeed * 2.23694).toFixed(0);

    // Calculate surf rating (0-10)
    let rating = 5; // Default
    if (waveHeight >= 1.2 && waveHeight <= 2.0 && dominantPeriod >= 8) {
      rating = 8; // Good conditions
    } else if (waveHeight >= 0.6 && waveHeight <= 1.2) {
      rating = 6; // Fair conditions
    } else if (waveHeight < 0.6) {
      rating = 3; // Small/flat
    } else if (waveHeight > 2.5) {
      rating = 7; // Big but potentially challenging
    }

    // Adjust for wind
    if (windSpeed > 15) {
      rating = Math.max(1, rating - 2); // Strong winds reduce quality
    }

    // Generate conditions description
    let conditions = '';
    if (waveHeight < 0.6) {
      conditions = 'Small waves, flat conditions. Better for beginners.';
    } else if (waveHeight >= 0.6 && waveHeight < 1.2) {
      conditions = 'Fair surf with manageable waves. Good for all skill levels.';
    } else if (waveHeight >= 1.2 && waveHeight < 2.0) {
      conditions = 'Good surf conditions with clean waves. Ideal for intermediate to advanced surfers.';
    } else {
      conditions = 'Large waves present. Recommended for experienced surfers only.';
    }

    if (windSpeed > 15) {
      conditions += ' Strong winds may affect wave quality.';
    } else if (windSpeed < 10) {
      conditions += ' Light winds creating clean conditions.';
    }

    const surfReport: SurfReportData = {
      date: new Date().toISOString().split('T')[0],
      source: 'NOAA Buoy 41004',
      wave_height: `${waveHeightFt} ft`,
      wave_period: `${dominantPeriod.toFixed(0)} sec`,
      swell_direction: getDirection(waveDirection),
      conditions,
      rating,
      raw_data: {
        buoy_id: BUOY_ID,
        wave_height_m: waveHeight,
        dominant_period_s: dominantPeriod,
        wave_direction_deg: waveDirection,
        wind_speed_mph: windSpeedMph,
        timestamp: new Date().toISOString()
      }
    };

    // Store in database
    const { data, error } = await supabase
      .from('external_surf_reports')
      .upsert(surfReport, {
        onConflict: 'date,source',
        ignoreDuplicates: false
      })
      .select()
      .single();

    if (error) {
      console.error('Error storing surf report:', error);
      throw error;
    }

    console.log('Surf report stored successfully:', data);

    return new Response(
      JSON.stringify({ success: true, data }),
      {
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  } catch (error) {
    console.error('Error fetching surf report:', error);
    return new Response(
      JSON.stringify({ success: false, error: error.message }),
      {
        status: 500,
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  }
});

function getDirection(degrees: number): string {
  const directions = ['N', 'NNE', 'NE', 'ENE', 'E', 'ESE', 'SE', 'SSE', 'S', 'SSW', 'SW', 'WSW', 'W', 'WNW', 'NW', 'NNW'];
  const index = Math.round(degrees / 22.5) % 16;
  return directions[index];
}

import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

Deno.serve(async (req: Request) => {
  try {
    // Create Supabase client
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    console.log('Generating daily surf report...');

    const today = new Date().toISOString().split('T')[0];

    // Fetch all data for today
    const [weatherResult, tideResult, externalReportResult] = await Promise.all([
      supabase.from('weather_data').select('*').eq('date', today).single(),
      supabase.from('tide_data').select('*').eq('date', today).order('time'),
      supabase.from('external_surf_reports').select('*').eq('date', today).single()
    ]);

    const weather = weatherResult.data;
    const tides = tideResult.data || [];
    const externalReport = externalReportResult.data;

    if (!weather || !externalReport) {
      throw new Error('Missing required data for report generation');
    }

    // Format tide information
    const tideInfo = tides.map((tide: any) => {
      const time = new Date(`2000-01-01T${tide.time}`).toLocaleTimeString('en-US', {
        hour: 'numeric',
        minute: '2-digit',
        hour12: true
      });
      return `${tide.type.charAt(0).toUpperCase() + tide.type.slice(1)} tide at ${time} (${tide.height.toFixed(1)} ft)`;
    }).join(', ');

    // Generate comprehensive conditions text
    const conditions = `${externalReport.conditions} ${weather.forecast} Current air temperature is ${weather.temperature}°F with ${weather.conditions.toLowerCase()}. Water temperature is comfortable for surfing. ${tideInfo}.`;

    // Calculate overall rating
    const rating = externalReport.rating || 5;

    // Create or update surf report
    const surfReport = {
      date: today,
      wave_height: externalReport.wave_height,
      wave_period: externalReport.wave_period,
      swell_direction: externalReport.swell_direction,
      wind_speed: `${weather.wind_speed} mph`,
      wind_direction: weather.wind_direction,
      tide: tideInfo,
      tide_times: tides,
      water_temp: '72°F', // Approximate for Folly Beach
      air_temp: `${weather.temperature}°F`,
      weather_conditions: weather.conditions,
      conditions,
      rating
    };

    const { data, error } = await supabase
      .from('surf_reports')
      .upsert(surfReport, {
        onConflict: 'date',
        ignoreDuplicates: false
      })
      .select()
      .single();

    if (error) {
      console.error('Error storing daily report:', error);
      throw error;
    }

    console.log('Daily surf report generated successfully:', data);

    return new Response(
      JSON.stringify({ success: true, data }),
      {
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  } catch (error) {
    console.error('Error generating daily report:', error);
    return new Response(
      JSON.stringify({ success: false, error: error.message }),
      {
        status: 500,
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  }
});

import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

/**
 * This function is designed to be called by a cron job daily at 6:00 AM EST
 * It orchestrates the fetching of all weather, tide, and surf data
 * and generates a comprehensive daily report
 */

Deno.serve(async (req: Request) => {
  try {
    console.log('Starting daily data update...');

    // Create Supabase client
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    // Step 1: Fetch weather data
    console.log('Fetching weather data...');
    const weatherResponse = await supabase.functions.invoke('fetch-weather-data');
    if (weatherResponse.error) {
      console.error('Weather fetch error:', weatherResponse.error);
      throw new Error('Failed to fetch weather data');
    }
    console.log('Weather data fetched successfully');

    // Step 2: Fetch tide data
    console.log('Fetching tide data...');
    const tideResponse = await supabase.functions.invoke('fetch-tide-data');
    if (tideResponse.error) {
      console.error('Tide fetch error:', tideResponse.error);
      throw new Error('Failed to fetch tide data');
    }
    console.log('Tide data fetched successfully');

    // Step 3: Fetch surf reports from buoy
    console.log('Fetching surf reports...');
    const surfResponse = await supabase.functions.invoke('fetch-surf-reports');
    if (surfResponse.error) {
      console.error('Surf fetch error:', surfResponse.error);
      throw new Error('Failed to fetch surf reports');
    }
    console.log('Surf reports fetched successfully');

    // Step 4: Generate comprehensive daily report
    console.log('Generating daily report...');
    const reportResponse = await supabase.functions.invoke('generate-daily-report');
    if (reportResponse.error) {
      console.error('Report generation error:', reportResponse.error);
      throw new Error('Failed to generate daily report');
    }
    console.log('Daily report generated successfully');

    const result = {
      success: true,
      timestamp: new Date().toISOString(),
      message: 'Daily update completed successfully',
      data: {
        weather: weatherResponse.data,
        tide: tideResponse.data,
        surf: surfResponse.data,
        report: reportResponse.data
      }
    };

    console.log('Daily update complete:', result);

    return new Response(
      JSON.stringify(result),
      {
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  } catch (error) {
    console.error('Daily update error:', error);
    return new Response(
      JSON.stringify({
        success: false,
        error: error.message,
        timestamp: new Date().toISOString()
      }),
      {
        status: 500,
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  }
});

