-- Enable pg_cron extension if not already enabled
CREATE EXTENSION IF NOT EXISTS pg_cron;

-- Grant usage on cron schema to postgres
GRANT USAGE ON SCHEMA cron TO postgres;

-- Create a function to call the daily update edge function
CREATE OR REPLACE FUNCTION trigger_daily_update()
RETURNS void
LANGUAGE plpgsql
SECURITY DEFINER
AS $$
DECLARE
  service_role_key text;
  supabase_url text;
BEGIN
  -- Get the Supabase URL from the current database
  supabase_url := current_setting('app.settings.supabase_url', true);

  -- Call the edge function using pg_net (if available)
  -- Note: This requires the pg_net extension
  PERFORM net.http_post(
    url := supabase_url || '/functions/v1/daily-update-cron',
    headers := jsonb_build_object(
      'Content-Type', 'application/json',
      'Authorization', 'Bearer ' || current_setting('app.settings.service_role_key', true)
    ),
    body := '{}'::jsonb
  );

  RAISE NOTICE 'Daily update triggered at %', now();
EXCEPTION
  WHEN OTHERS THEN
    RAISE WARNING 'Error triggering daily update: %', SQLERRM;
END;
$$;

-- Schedule the cron job to run daily at 6:00 AM EST (11:00 AM UTC)
-- Note: Adjust the time based on your timezone
SELECT cron.schedule(
  'daily-surf-report-update',
  '0 11 * * *',  -- 11:00 AM UTC = 6:00 AM EST
  'SELECT trigger_daily_update();'
);

-- Create a table to log cron job executions
CREATE TABLE IF NOT EXISTS cron_job_logs (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  job_name TEXT NOT NULL,
  executed_at TIMESTAMPTZ DEFAULT now(),
  status TEXT,
  error_message TEXT
);

-- Enable RLS on cron_job_logs
ALTER TABLE cron_job_logs ENABLE ROW LEVEL SECURITY;

-- Create RLS policy for admins to view logs
CREATE POLICY "Enable read access for admins" ON cron_job_logs
  FOR SELECT
  TO PUBLIC
  USING (
    EXISTS (
      SELECT 1 FROM profiles
      WHERE profiles.id = auth.uid()
      AND profiles.is_admin = TRUE
    )
  );

