import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

const NOAA_STATION_ID = '8665530'; // Charleston, SC (closest to Folly Beach)

Deno.serve(async (req: Request) => {
  // Handle CORS preflight
  if (req.method === 'OPTIONS') {
    return new Response(null, {
      status: 200,
      headers: {
        'Access-Control-Allow-Origin': '*',
        'Access-Control-Allow-Methods': 'POST, OPTIONS',
        'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
      },
    });
  }

  try {
    console.log('Starting tide data fetch...');

    // Initialize Supabase client
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    // Get today's date and tomorrow's date for the API request
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);

    const beginDate = today.toISOString().split('T')[0].replace(/-/g, '');
    const endDate = tomorrow.toISOString().split('T')[0].replace(/-/g, '');

    console.log(`Fetching tide data for station ${NOAA_STATION_ID} from ${beginDate} to ${endDate}`);

    // Fetch tide predictions from NOAA
    const noaaUrl = `https://api.tidesandcurrents.noaa.gov/api/prod/datagetter?` +
      `product=predictions&application=NOS.COOPS.TAC.WL&` +
      `begin_date=${beginDate}&end_date=${endDate}&` +
      `datum=MLLW&station=${NOAA_STATION_ID}&time_zone=lst_ldt&` +
      `units=english&interval=hilo&format=json`;

    console.log('NOAA API URL:', noaaUrl);

    const noaaResponse = await fetch(noaaUrl);

    if (!noaaResponse.ok) {
      const errorText = await noaaResponse.text();
      console.error('NOAA API error:', noaaResponse.status, errorText);
      throw new Error(`NOAA API returned ${noaaResponse.status}: ${errorText}`);
    }

    const noaaData = await noaaResponse.json();
    console.log('NOAA response:', JSON.stringify(noaaData, null, 2));

    if (!noaaData.predictions || noaaData.predictions.length === 0) {
      console.error('No tide predictions in response');
      throw new Error('No tide predictions available from NOAA');
    }

    // Get today's date string for filtering
    const todayStr = today.toISOString().split('T')[0];
    console.log('Today date string:', todayStr);

    // Delete existing tide data for today
    const { error: deleteError } = await supabase
      .from('tide_data')
      .delete()
      .eq('date', todayStr);

    if (deleteError) {
      console.error('Error deleting old tide data:', deleteError);
      throw deleteError;
    }

    console.log('Deleted old tide data for today');

    // Parse and insert tide data
    const tideRecords = noaaData.predictions
      .filter((prediction: any) => {
        // Only include today's tides
        const predictionDate = prediction.t.split(' ')[0];
        return predictionDate === todayStr;
      })
      .map((prediction: any) => {
        // Parse the datetime string (format: "YYYY-MM-DD HH:MM")
        const [datePart, timePart] = prediction.t.split(' ');
        const type = prediction.type.toLowerCase(); // 'H' or 'L'
        const height = parseFloat(prediction.v);

        return {
          date: datePart,
          time: timePart,
          type: type === 'h' ? 'high' : 'low',
          height: height,
        };
      });

    console.log(`Parsed ${tideRecords.length} tide records for today:`, JSON.stringify(tideRecords, null, 2));

    if (tideRecords.length === 0) {
      console.warn('No tide records for today after filtering');
      return new Response(
        JSON.stringify({ 
          success: true, 
          message: 'No tide data for today',
          count: 0 
        }),
        {
          status: 200,
          headers: {
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin': '*',
          },
        }
      );
    }

    // Insert new tide data
    const { data: insertedData, error: insertError } = await supabase
      .from('tide_data')
      .insert(tideRecords)
      .select();

    if (insertError) {
      console.error('Error inserting tide data:', insertError);
      throw insertError;
    }

    console.log(`Successfully inserted ${insertedData?.length || 0} tide records`);

    return new Response(
      JSON.stringify({ 
        success: true, 
        message: 'Tide data updated successfully',
        count: insertedData?.length || 0,
        data: insertedData
      }),
      {
        status: 200,
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*',
        },
      }
    );
  } catch (error) {
    console.error('Error in fetch-tide-data function:', error);
    return new Response(
      JSON.stringify({ 
        success: false, 
        error: error instanceof Error ? error.message : 'Unknown error',
        details: error instanceof Error ? error.stack : undefined
      }),
      {
        status: 500,
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*',
        },
      }
    );
  }
});

