        } else {
          conditions += `Very strong ${windDir} winds at ${windSpeedMph} mph are making conditions challenging with significant chop and wind spray. Only for the determined! `;
        }

        // Wind direction analysis (offshore is best)
        if (windDirection) {
          // For Folly Beach (SE facing), W-NW winds are offshore
          if (windDirection >= 270 || windDirection <= 45) {
            conditions += 'Offshore winds are grooming the waves, holding up the faces, and improving shape dramatically. Premium conditions! ';
          } else if ((windDirection >= 225 && windDirection < 270) || (windDirection > 45 && windDirection <= 90)) {
            conditions += 'Side-shore winds are affecting wave quality but conditions remain manageable. ';
          } else if (windDirection >= 90 && windDirection <= 180) {
            conditions += 'Onshore winds are degrading wave quality, creating bumpy faces and making waves crumble. ';
          }
        }
      }
    }

    const surfReport: SurfReportData = {
      date: new Date().toISOString().split('T')[0],
      source: 'NOAA Buoy 41004',
      wave_height: `${waveHeightFt} ft`,
      wave_period: period ? `${period.toFixed(0)} sec` : '8-10 sec',
      swell_direction: waveDirection ? getDirection(waveDirection) : 'Variable',
      conditions,
      rating,
      raw_data: {
        buoy_id: BUOY_ID,
        wave_height_m: waveHeight,
        dominant_period_s: dominantPeriod,
        avg_period_s: avgPeriod,
        wave_direction_deg: waveDirection,
        wind_speed_mph: windSpeedMph,
        wind_direction_deg: windDirection,
        water_temp_f: waterTempF,
        air_temp_f: airTempF,
        timestamp: new Date().toISOString(),
        raw_line: latestData.join(' ')
      }
    };

    console.log('Storing surf report:', surfReport);

    // Store in database
    const { data, error } = await supabase
      .from('external_surf_reports')
      .upsert(surfReport, {
        onConflict: 'date,source',
        ignoreDuplicates: false
      })
      .select()
      .single();

    if (error) {
      console.error('Error storing surf report:', error);
      throw error;
    }

    console.log('Surf report stored successfully:', data);

    return new Response(
      JSON.stringify({ success: true, data }),
      {
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  } catch (error) {
    console.error('Error fetching surf report:', error);
    return new Response(
      JSON.stringify({ success: false, error: error.message }),
      {
        status: 500,
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  }
});

function getDirection(degrees: number): string {
  const directions = ['N', 'NNE', 'NE', 'ENE', 'E', 'ESE', 'SE', 'SSE', 'S', 'SSW', 'SW', 'WSW', 'W', 'WNW', 'NW', 'NNW'];
  const index = Math.round(degrees / 22.5) % 16;
  return directions[index];
}

import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

// Clever quips for when conditions are unavailable (likely flat/small)
const unavailableQuips = [
  "Surf conditions data is playing hide and seek with us today - much like the waves themselves! When the buoy goes quiet, it usually means Mother Ocean is taking a breather with waves too small to register. Perfect time to wax your board and dream of bigger days ahead.",
  "The NOAA buoy is giving us the silent treatment, which typically means the waves are so tiny they're practically invisible to the sensors. Think ankle-slappers at best. Great day for a beach walk or practicing your pop-ups on dry land!",
  "Our buoy friend seems to have dozed off - probably because there's not much wave action to report. When conditions are this flat, even the instruments get bored. Time to check out the local surf shop or plan your next surf trip!",
  "Looks like the ocean hit the snooze button today! Data unavailable usually translates to 'waves smaller than a surfboard is tall.' Perfect conditions for beginners to practice in the shallows or for everyone else to catch up on beach reading.",
  "The buoy's taking a coffee break, which in surf-speak means the waves are flatter than a pancake. When readings drop below detection levels, we're talking ripples, not rideable waves. Maybe it's a sign to try SUP or kayaking instead?",
  "Radio silence from the buoy typically means the Atlantic is serving up a big plate of nothing. Waves so small they don't even register on the instruments. Silver lining? The water's probably super calm for a swim!"
