Deno.serve(async (req: Request) => {
  try {
    // Create Supabase client
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    console.log('Generating daily surf report...');

    const today = new Date().toISOString().split('T')[0];

    // Fetch all data for today
    const [weatherResult, tideResult, externalReportResult] = await Promise.all([
      supabase.from('weather_data').select('*').eq('date', today).maybeSingle(),
      supabase.from('tide_data').select('*').eq('date', today).order('time'),
      supabase.from('external_surf_reports').select('*').eq('date', today).maybeSingle()
    ]);

    console.log('Weather result:', weatherResult);
    console.log('Tide result:', tideResult);
    console.log('External report result:', externalReportResult);

    const weather = weatherResult.data;
    const tides = tideResult.data || [];
    const externalReport = externalReportResult.data;

    if (!weather) {
      throw new Error('Missing weather data for report generation');
    }

    if (!externalReport) {
      throw new Error('Missing surf report data for report generation');
    }

    // Format tide information with detailed descriptions
    let tideInfo = 'Tide data unavailable';
    let tideDescription = '';
    if (tides.length > 0) {
      const tideStrings = tides.map((tide: any) => {
        const time = new Date(`2000-01-01T${tide.time}`).toLocaleTimeString('en-US', {
          hour: 'numeric',
          minute: '2-digit',
          hour12: true
        });
        return `${tide.type.charAt(0).toUpperCase() + tide.type.slice(1)} tide at ${time} (${tide.height.toFixed(1)} ft)`;
      });
      tideInfo = tideStrings.join(', ');

      // Add tide analysis
      const now = new Date();
      const currentTime = now.getHours() * 60 + now.getMinutes();

      // Find next tide
      let nextTide = null;
      for (const tide of tides) {
        const [hours, minutes] = tide.time.split(':').map(Number);
        const tideMinutes = hours * 60 + minutes;
        if (tideMinutes > currentTime) {
          nextTide = tide;
          break;
        }
      }

      if (nextTide) {
        const [hours, minutes] = nextTide.time.split(':').map(Number);
        const tideTime = new Date();
        tideTime.setHours(hours, minutes, 0);
        const timeUntil = Math.round((tideTime.getTime() - now.getTime()) / (1000 * 60));

        if (timeUntil > 0 && timeUntil < 180) {
          tideDescription = `Next ${nextTide.type} tide is in ${timeUntil} minutes. `;

          if (nextTide.type === 'low') {
            tideDescription += 'Low tide typically offers better wave shape, more exposed sandbars, and shallower water that helps waves break more consistently. Ideal for most surf sessions. ';
          } else {
            tideDescription += 'High tide may cover some sandbars but can provide deeper water for larger waves to roll through. Some spots work better on the push. ';
          }
        }
      }

      // Tide height analysis
      const avgHeight = tides.reduce((sum: number, t: any) => sum + t.height, 0) / tides.length;
      if (avgHeight > 5.5) {
        tideDescription += 'Higher than average tides today - expect fuller beach coverage and deeper water. ';
      } else if (avgHeight < 4.5) {
        tideDescription += 'Lower than average tides today - more exposed beach and sandbars. ';
      }

      // Tide range analysis
      const heights = tides.map((t: any) => t.height);
      const tideRange = Math.max(...heights) - Math.min(...heights);
      if (tideRange > 5) {
        tideDescription += 'Large tidal range today means significant changes in conditions throughout the day. ';
      } else if (tideRange < 3) {
        tideDescription += 'Minimal tidal range means more consistent conditions throughout the day. ';
      }
    }

    // Get water temperature from buoy data if available
    const waterTemp = externalReport.raw_data?.water_temp_f 
      ? `${externalReport.raw_data.water_temp_f}°F` 
      : '72°F'; // Fallback approximate for Folly Beach

    const airTemp = externalReport.raw_data?.air_temp_f
      ? `${externalReport.raw_data.air_temp_f}°F`
      : `${weather.temperature}°F`;

    // Generate comprehensive conditions text that describes waves in detail
    let conditions = '';

    // Check if external report has unavailable data (contains one of our quips)
    const hasUnavailableData = unavailableQuips.some(quip => 
      externalReport.conditions.includes(quip.substring(0, 50))
    );

    if (hasUnavailableData) {
      // Use the quip from the external report
      conditions = externalReport.conditions + ' ';
    } else {
      // Use the detailed conditions from external report
      conditions = externalReport.conditions + ' ';
    }

    // Add weather context
    if (weather.conditions) {
      const weatherLower = weather.conditions.toLowerCase();
      conditions += `Weather conditions are ${weatherLower} with an air temperature of ${airTemp}. `;

      // Add weather-specific surf advice
      if (weatherLower.includes('rain') || weatherLower.includes('storm')) {
        conditions += 'Rainy conditions may reduce visibility but can sometimes coincide with good swell. Check lightning forecasts before heading out. ';
      } else if (weatherLower.includes('clear') || weatherLower.includes('sunny')) {
        conditions += 'Clear skies make for excellent visibility and comfortable surf sessions. Don\'t forget sunscreen! ';
      } else if (weatherLower.includes('cloud')) {
        conditions += 'Overcast conditions provide natural sun protection and can make for comfortable all-day sessions. ';
      }
    }

    // Add water temperature context with detailed recommendations
    const waterTempNum = parseInt(waterTemp);
    if (waterTempNum < 55) {
      conditions += `Water is very cold at ${waterTemp} - 5/4mm or 6/5mm wetsuit with boots, gloves, and hood essential. Hypothermia risk is real. `;
    } else if (waterTempNum < 60) {
      conditions += `Water is cold at ${waterTemp} - 4/3mm or 5/4mm full wetsuit with boots and gloves recommended. Limit session length. `;
    } else if (waterTempNum < 65) {
      conditions += `Water is cool at ${waterTemp} - 4/3mm full wetsuit recommended for comfort during longer sessions. `;
    } else if (waterTempNum < 68) {
      conditions += `Water is refreshing at ${waterTemp} - 3/2mm full wetsuit or spring suit recommended. Some hardy souls go with boardshorts. `;
    } else if (waterTempNum < 72) {
      conditions += `Water temperature is comfortable at ${waterTemp} - spring suit, shorty wetsuit, or boardshorts all work well. `;
    } else if (waterTempNum < 75) {
      conditions += `Water is pleasant at ${waterTemp} - boardshorts or bikini weather with optional rash guard for sun protection. `;
    } else if (waterTempNum < 80) {
      conditions += `Water is warm at ${waterTemp} - boardshorts or bikini weather. Rash guard recommended for sun protection only. `;
    } else {
      conditions += `Water is bath-like at ${waterTemp} - minimal coverage needed. Just don't forget the sunscreen! `;
    }

    // Add tide information
    conditions += `**Tide Schedule:** ${tideInfo}. `;
    if (tideDescription) {
      conditions += tideDescription;
    }

    // Add forecast context if available
    if (weather.forecast) {
      conditions += `**Forecast:** ${weather.forecast} `;
    }

    // Add time-specific recommendations
    const hour = new Date().getHours();
    if (hour < 7) {
      conditions += '**Dawn Patrol:** You\'re up early! Dawn sessions often provide the glassiest conditions before winds develop. Magic hour for photography too. ';
    } else if (hour < 10) {
      conditions += '**Morning Session:** Early morning typically offers the cleanest conditions before winds pick up and crowds arrive. Prime time! ';
    } else if (hour < 14) {
      conditions += '**Midday Session:** Winds may be picking up and crowds are likely present. Check multiple spots for best conditions. ';
    } else if (hour < 17) {
      conditions += '**Afternoon Session:** Winds are typically strongest now. Look for protected spots or wait for the evening glass-off. ';
    } else if (hour < 20) {
      conditions += '**Evening Session:** Winds often calm down as the sun sets, creating a second window of clean conditions. Beautiful time to be in the water. ';
    } else {
      conditions += '**Night Session:** Limited visibility - only recommended for experienced surfers at familiar breaks with good lighting. Safety first! ';
    }

    // Add crowd and safety considerations
    const dayOfWeek = new Date().getDay();
    if (dayOfWeek === 0 || dayOfWeek === 6) {
      conditions += 'Weekend crowds expected - arrive early for parking and less crowded peaks. ';
    }

    // Calculate overall rating (use external report rating as base)
    const rating = externalReport.rating || 5;

    // Create or update surf report
    const surfReport = {
      date: today,
      wave_height: externalReport.wave_height,
      wave_period: externalReport.wave_period,
      swell_direction: externalReport.swell_direction,
      wind_speed: `${weather.wind_speed} mph`,
      wind_direction: weather.wind_direction,
      tide: tideInfo,
      tide_times: tides,
      water_temp: waterTemp,
      air_temp: airTemp,
      weather_conditions: weather.conditions,
      conditions,
      rating
    };

    console.log('Creating surf report:', surfReport);

    // Check if there's already a report with custom text
    const { data: existingReport } = await supabase
      .from('surf_reports')
      .select('report_text, edited_by, edited_at')
      .eq('date', today)
      .maybeSingle();

    // Preserve custom report text if it exists
    if (existingReport?.report_text) {
      console.log('Preserving existing custom report text');
      surfReport.report_text = existingReport.report_text;
      surfReport.edited_by = existingReport.edited_by;
      surfReport.edited_at = existingReport.edited_at;
    }

    const { data, error } = await supabase
      .from('surf_reports')
      .upsert(surfReport, {
        onConflict: 'date',
        ignoreDuplicates: false
      })
      .select()
      .single();

    if (error) {
      console.error('Error storing daily report:', error);
      throw error;
    }

    console.log('Daily surf report generated successfully:', data);

    return new Response(
      JSON.stringify({ success: true, data }),
      {
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  } catch (error) {
    console.error('Error generating daily report:', error);
    return new Response(
      JSON.stringify({ success: false, error: error.message }),
      {
        status: 500,
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  }
});

import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

// Folly Beach coordinates
const FOLLY_BEACH_LAT = 32.6552;
const FOLLY_BEACH_LON = -79.9403;

// NOAA API endpoints
const NOAA_POINTS_API = `https://api.weather.gov/points/${FOLLY_BEACH_LAT},${FOLLY_BEACH_LON}`;

interface WeatherData {
  date: string;
  temperature: number;
  feels_like: number;
  humidity: number;
  wind_speed: number;
  wind_direction: string;
  wind_gust: number;
  pressure: number;
  visibility: number;
  conditions: string;
  forecast: string;
  raw_data: any;
}

interface WeatherForecast {
  date: string;
  day_name: string;
  high_temp: number;
  low_temp: number;
  conditions: string;
  icon: string;
  wind_speed: number;
  wind_direction: string;
  precipitation_chance: number;
  humidity: number;
  swell_height_min?: number;
  swell_height_max?: number;
  swell_height_range?: string;
}

// Function to estimate swell size based on date offset from today
function estimateSwellSize(daysFromToday: number, currentSwellHeight: number): { min: number; max: number; range: string } {
  // Base swell height with some variation
  const baseMin = Math.max(0.5, currentSwellHeight - 0.5 + (Math.random() * 0.5 - 0.25));
  const baseMax = currentSwellHeight + 0.5 + (Math.random() * 0.5 - 0.25);

  // Add some variation based on days out (further out = more uncertainty)
  const variation = daysFromToday * 0.2;
  const min = Math.max(0.5, baseMin + (Math.random() * variation - variation / 2));
  const max = baseMax + (Math.random() * variation - variation / 2);

  // Round to 1 decimal place
  const minRounded = Math.round(min * 10) / 10;
  const maxRounded = Math.round(max * 10) / 10;

  return {
    min: minRounded,
    max: maxRounded,
    range: `${minRounded}-${maxRounded} ft`
  };
}

Deno.serve(async (req: Request) => {
  try {
    // Create Supabase client
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    console.log('Fetching NOAA weather data for Folly Beach...');

    // Step 1: Get the forecast office and grid coordinates
    const pointsResponse = await fetch(NOAA_POINTS_API, {
      headers: {
        'User-Agent': 'SurfVista App (contact@surfvista.com)',
        'Accept': 'application/json'
      }
    });

    if (!pointsResponse.ok) {
      throw new Error(`NOAA Points API error: ${pointsResponse.status}`);
    }

    const pointsData = await pointsResponse.json();
    const forecastUrl = pointsData.properties.forecast;
    const observationStationsUrl = pointsData.properties.observationStations;

    // Step 2: Get the forecast
    const forecastResponse = await fetch(forecastUrl, {
      headers: {
        'User-Agent': 'SurfVista App (contact@surfvista.com)',
        'Accept': 'application/json'
      }
    });

    if (!forecastResponse.ok) {
      throw new Error(`NOAA Forecast API error: ${forecastResponse.status}`);
    }

    const forecastData = await forecastResponse.json();
    const periods = forecastData.properties.periods;
    const todayForecast = periods[0];

    // Step 3: Get observation stations
    const stationsResponse = await fetch(observationStationsUrl, {
      headers: {
        'User-Agent': 'SurfVista App (contact@surfvista.com)',
        'Accept': 'application/json'
      }
    });

    if (!stationsResponse.ok) {
      throw new Error(`NOAA Stations API error: ${stationsResponse.status}`);
    }

    const stationsData = await stationsResponse.json();
    const nearestStation = stationsData.features[0]?.id;

    // Step 4: Get current observations
    let currentObservation = null;
    if (nearestStation) {
      const observationUrl = `https://api.weather.gov/stations/${nearestStation}/observations/latest`;
      const observationResponse = await fetch(observationUrl, {
        headers: {
          'User-Agent': 'SurfVista App (contact@surfvista.com)',
          'Accept': 'application/json'
        }
      });

      if (observationResponse.ok) {
        currentObservation = await observationResponse.json();
      }
    }

    // Step 5: Get current swell data from external_surf_reports
    const today = new Date().toISOString().split('T')[0];
    const { data: surfData } = await supabase
      .from('external_surf_reports')
      .select('wave_height, raw_data')
      .eq('date', today)
      .maybeSingle();

    // Parse current wave height
    let currentSwellHeight = 2.0; // Default fallback
    if (surfData?.raw_data?.wave_height_m) {
      currentSwellHeight = surfData.raw_data.wave_height_m * 3.28084; // Convert meters to feet
    } else if (surfData?.wave_height) {
      // Try to parse from string like "2.5 ft" or "2-3 ft"
      const match = surfData.wave_height.match(/([0-9.]+)/);
      if (match) {
        currentSwellHeight = parseFloat(match[1]);
      }
    }

    console.log('Current swell height:', currentSwellHeight, 'ft');

    // Extract weather data
    const obs = currentObservation?.properties;
    const weatherData: WeatherData = {
      date: today,
      temperature: obs?.temperature?.value ? Math.round((obs.temperature.value * 9/5) + 32) : todayForecast.temperature,
      feels_like: obs?.heatIndex?.value ? Math.round((obs.heatIndex.value * 9/5) + 32) : todayForecast.temperature,
      humidity: obs?.relativeHumidity?.value || 0,
      wind_speed: obs?.windSpeed?.value ? Math.round(obs.windSpeed.value * 0.621371) : parseInt(todayForecast.windSpeed) || 0,
      wind_direction: obs?.windDirection?.value ? getWindDirection(obs.windDirection.value) : todayForecast.windDirection || 'N',
      wind_gust: obs?.windGust?.value ? Math.round(obs.windGust.value * 0.621371) : 0,
      pressure: obs?.barometricPressure?.value ? Math.round(obs.barometricPressure.value / 100) : 0,
      visibility: obs?.visibility?.value ? Math.round(obs.visibility.value / 1609.34) : 10,
      conditions: todayForecast.shortForecast || 'Unknown',
      forecast: todayForecast.detailedForecast || '',
      raw_data: {
        observation: obs,
        forecast: todayForecast
      }
    };

    // Store current weather in database
    const { data: weatherResult, error: weatherError } = await supabase
      .from('weather_data')
      .upsert(weatherData, {
        onConflict: 'date',
        ignoreDuplicates: false
      })
      .select()
      .single();

    if (weatherError) {
      console.error('Error storing weather data:', weatherError);
      throw weatherError;
    }

    console.log('Weather data stored successfully:', weatherResult);

    // Process 7-day forecast
    const forecastRecords: WeatherForecast[] = [];
    const processedDates = new Set<string>();

    for (let i = 0; i < periods.length && forecastRecords.length < 7; i++) {
      const period = periods[i];
      const periodDate = new Date(period.startTime);
      const dateStr = periodDate.toISOString().split('T')[0];

      // Skip if we already processed this date
      if (processedDates.has(dateStr)) {
        continue;
      }

      processedDates.add(dateStr);

      // Calculate days from today for swell estimation
      const todayDate = new Date(today);
      const daysFromToday = Math.floor((periodDate.getTime() - todayDate.getTime()) / (1000 * 60 * 60 * 24));

      // Estimate swell size for this day
      const swellEstimate = estimateSwellSize(daysFromToday, currentSwellHeight);

      // Find the corresponding night period for low temp
      let lowTemp = period.temperature;
      let highTemp = period.temperature;

      if (period.isDaytime) {
        highTemp = period.temperature;
        // Look for the night period
        const nightPeriod = periods.find((p: any, idx: number) => 
          idx > i && 
          !p.isDaytime && 
          new Date(p.startTime).toISOString().split('T')[0] === dateStr
        );
        if (nightPeriod) {
          lowTemp = nightPeriod.temperature;
        }
      } else {
        lowTemp = period.temperature;
        // Look for the day period
        const dayPeriod = periods.find((p: any, idx: number) => 
          idx > i && 
          p.isDaytime && 
          new Date(p.startTime).toISOString().split('T')[0] === dateStr
        );
        if (dayPeriod) {
          highTemp = dayPeriod.temperature;
        }
      }

      const windSpeedMatch = period.windSpeed.match(/\d+/);
      const windSpeed = windSpeedMatch ? parseInt(windSpeedMatch[0]) : 0;

      forecastRecords.push({
        date: dateStr,
        day_name: periodDate.toLocaleDateString('en-US', { weekday: 'short' }),
        high_temp: highTemp,
        low_temp: lowTemp,
        conditions: period.shortForecast,
        icon: period.icon,
        wind_speed: windSpeed,
        wind_direction: period.windDirection,
        precipitation_chance: period.probabilityOfPrecipitation?.value || 0,
        humidity: obs?.relativeHumidity?.value || 0,
        swell_height_min: swellEstimate.min,
        swell_height_max: swellEstimate.max,
        swell_height_range: swellEstimate.range
      });
    }

    // Store forecast in database
    if (forecastRecords.length > 0) {
      const { data: forecastResult, error: forecastError } = await supabase
        .from('weather_forecast')
        .upsert(forecastRecords, {
          onConflict: 'date',
          ignoreDuplicates: false
        })
        .select();

      if (forecastError) {
        console.error('Error storing forecast data:', forecastError);
        throw forecastError;
      }

      console.log(`Stored ${forecastResult.length} forecast records successfully`);
    }

    return new Response(
      JSON.stringify({ 
        success: true, 
        weather: weatherResult,
        forecast_count: forecastRecords.length
      }),
      {
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  } catch (error) {
    console.error('Error fetching weather data:', error);
    return new Response(
      JSON.stringify({ success: false, error: error.message }),
      {
        status: 500,
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );
  }
});

function getWindDirection(degrees: number): string {
  const directions = ['N', 'NNE', 'NE', 'ENE', 'E', 'ESE', 'SE', 'SSE', 'S', 'SSW', 'SW', 'WSW', 'W', 'WNW', 'NW', 'NNW'];
  const index = Math.round(degrees / 22.5) % 16;
  return directions[index];
}

