      .maybeSingle();

    console.log('Weather data result:', {
      error: weatherResult.error,
      hasData: !!weatherResult.data,
    });

    console.log('Fetching tide data...');
    const tideResult = await supabase
      .from('tide_data')
      .select('*')
      .eq('date', today)
      .order('time');

    console.log('Tide data result:', {
      error: tideResult.error,
      count: tideResult.data?.length || 0,
    });

    if (surfResult.error) {
      console.error('Error fetching surf conditions:', surfResult.error);
      return new Response(
        JSON.stringify({
          success: false,
          error: 'Failed to fetch surf conditions',
          details: surfResult.error.message,
          timestamp: new Date().toISOString(),
        }),
        {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
          status: 500,
        }
      );
    }

    if (weatherResult.error) {
      console.error('Error fetching weather:', weatherResult.error);
      return new Response(
        JSON.stringify({
          success: false,
          error: 'Failed to fetch weather data',
          details: weatherResult.error.message,
          timestamp: new Date().toISOString(),
        }),
        {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
          status: 500,
        }
      );
    }

    if (tideResult.error) {
      console.error('Error fetching tides:', tideResult.error);
      return new Response(
        JSON.stringify({
          success: false,
          error: 'Failed to fetch tide data',
          details: tideResult.error.message,
          timestamp: new Date().toISOString(),
        }),
        {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
          status: 500,
        }
      );
    }

    const surfData = surfResult.data;
    const weatherData = weatherResult.data;
    const tideData = tideResult.data || [];

    console.log('Data retrieved:', {
      hasSurf: !!surfData,
      hasWeather: !!weatherData,
      tideCount: tideData.length,
    });

    if (!surfData || !weatherData) {
      const missingData = [];
      if (!surfData) missingData.push('surf conditions');
      if (!weatherData) missingData.push('weather data');

      const errorMsg = `Missing required data for report generation: ${missingData.join(', ')}`;
      console.error(errorMsg);
      return new Response(
        JSON.stringify({
          success: false,
          error: errorMsg,
          timestamp: new Date().toISOString(),
        }),
        {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
          status: 500,
        }
      );
    }

    // Generate tide summary
    const tideSummary = generateTideSummary(tideData);
    console.log('Tide summary:', tideSummary);

    // Calculate surf rating (1-10)
    const rating = calculateSurfRating(surfData, weatherData);
    console.log('Calculated rating:', rating);

    // Generate report text
    const reportText = generateReportText(surfData, weatherData, tideSummary, rating);
    console.log('Generated report text length:', reportText.length);

    // Create the surf report
    const surfReport = {
      date: today,
      wave_height: surfData.wave_height,
      wave_period: surfData.wave_period || 'N/A',
      swell_direction: surfData.swell_direction || 'N/A',
      wind_speed: surfData.wind_speed,
      wind_direction: surfData.wind_direction,
      water_temp: surfData.water_temp,
      tide: tideSummary,
      conditions: reportText,
      rating: rating,
      updated_at: new Date().toISOString(),
    };

    console.log('Storing surf report...');

    // Store the report
    const { data: insertData, error: reportError } = await supabase
      .from('surf_reports')
      .upsert(surfReport, { onConflict: 'date' })
      .select();

    if (reportError) {
      console.error('Error storing surf report:', reportError);
      return new Response(
        JSON.stringify({
          success: false,
          error: 'Failed to store surf report in database',
          details: reportError.message,
          timestamp: new Date().toISOString(),
        }),
        {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
          status: 500,
        }
      );
    }

    console.log('Surf report stored successfully');
    console.log('=== GENERATE DAILY REPORT COMPLETED ===');

    return new Response(
      JSON.stringify({
        success: true,
        message: 'Daily surf report generated successfully for Folly Beach, SC',
        location: 'Folly Beach, SC',
        report: surfReport,
        timestamp: new Date().toISOString(),
      }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 200,
      }
    );
  } catch (error) {
    console.error('=== GENERATE DAILY REPORT FAILED ===');
    console.error('Error in generate-daily-report:', error);
    console.error('Stack trace:', error instanceof Error ? error.stack : 'N/A');

    return new Response(
      JSON.stringify({
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
        stack: error instanceof Error ? error.stack : undefined,
        timestamp: new Date().toISOString(),
      }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 500,
      }
    );
  }
});

function generateTideSummary(tideData: any[]): string {
  if (tideData.length === 0) {
    return 'Tide data unavailable';
  }

  const tides = tideData.map(t => `${t.type} at ${t.time} (${t.height}${t.height_unit})`);
  return tides.join(', ');
}

function calculateSurfRating(surfData: any, weatherData: any): number {
  let rating = 5; // Start at middle

  // Parse wave height
  const waveHeightMatch = surfData.wave_height.match(/(\d+\.?\d*)/);
  const waveHeight = waveHeightMatch ? parseFloat(waveHeightMatch[1]) : 0;

  // Wave height scoring (0-10 ft range)
  if (waveHeight >= 3 && waveHeight <= 6) {
    rating += 2; // Ideal range
  } else if (waveHeight >= 2 && waveHeight < 3) {
    rating += 1; // Decent
  } else if (waveHeight > 6 && waveHeight <= 8) {
    rating += 1; // Big but manageable
  } else if (waveHeight < 2) {
    rating -= 2; // Too small
  } else if (waveHeight > 8) {
    rating -= 1; // Too big for most
  }

  // Wind scoring
  const windSpeedMatch = surfData.wind_speed.match(/(\d+)/);
  const windSpeed = windSpeedMatch ? parseInt(windSpeedMatch[1]) : 0;

  const windDir = surfData.wind_direction.toLowerCase();

  // Offshore winds (W, NW, N) are best
  if (windDir.includes('w') || windDir.includes('n')) {
    if (windSpeed < 15) {
      rating += 2; // Light offshore
    } else if (windSpeed < 20) {
      rating += 1; // Moderate offshore
    }
  } else if (windDir.includes('e') || windDir.includes('s')) {
    // Onshore winds (E, SE, S) are worse
    if (windSpeed > 15) {
      rating -= 2; // Strong onshore
    } else {
      rating -= 1; // Light onshore
    }
  }

  // Wave period scoring
  const periodMatch = surfData.wave_period.match(/(\d+)/);
  const period = periodMatch ? parseInt(periodMatch[1]) : 0;

  if (period >= 10) {
    rating += 1; // Long period = better waves
  } else if (period < 6) {
    rating -= 1; // Short period = choppy
  }

  // Clamp rating between 1 and 10
  return Math.max(1, Math.min(10, rating));
}

function generateReportText(surfData: any, weatherData: any, tideSummary: string, rating: number): string {
  const waveHeightMatch = surfData.wave_height.match(/(\d+\.?\d*)/);
  const waveHeight = waveHeightMatch ? parseFloat(waveHeightMatch[1]) : 0;

  const windSpeedMatch = surfData.wind_speed.match(/(\d+)/);
  const windSpeed = windSpeedMatch ? parseInt(windSpeedMatch[1]) : 0;

  const windDir = surfData.wind_direction;
  const swellDir = surfData.swell_direction;
  const period = surfData.wave_period;

  let report = '';

  // Overall assessment
  if (rating >= 8) {
    report += 'Excellent surf conditions today at Folly Beach! ';
  } else if (rating >= 6) {
    report += 'Good surf conditions at Folly Beach with rideable waves. ';
  } else if (rating >= 4) {
    report += 'Fair surf conditions at Folly Beach. ';
  } else {
    report += 'Poor surf conditions at Folly Beach today. ';
  }

  // Wave description
  if (waveHeight >= 4) {
    report += `Waves are running ${surfData.wave_height} with ${period} period from ${swellDir}. `;
  } else if (waveHeight >= 2) {
    report += `Waves are ${surfData.wave_height} with ${period} period from ${swellDir}. `;
  } else {
    report += `Small waves at ${surfData.wave_height} with ${period} period from ${swellDir}. `;
  }

  // Wind conditions
  if (windDir.toLowerCase().includes('w') || windDir.toLowerCase().includes('n')) {
    if (windSpeed < 10) {
      report += `Light offshore winds from ${windDir} at ${surfData.wind_speed} creating clean conditions. `;
    } else if (windSpeed < 20) {
      report += `Offshore winds from ${windDir} at ${surfData.wind_speed} grooming the waves. `;
    } else {
      report += `Strong offshore winds from ${windDir} at ${surfData.wind_speed} may blow out the surf. `;
    }
  } else {
    if (windSpeed < 10) {
      report += `Light onshore winds from ${windDir} at ${surfData.wind_speed}. `;
    } else {
      report += `Onshore winds from ${windDir} at ${surfData.wind_speed} creating choppy conditions. `;
    }
  }

  // Water temperature
  report += `Water temperature is ${surfData.water_temp}. `;

  // Weather
  report += `${weatherData.short_forecast}. `;

  // Tide info
  report += `Tides: ${tideSummary}.`;

  return report;
}

import { serve } from 'https://deno.land/std@0.168.0/http/server.ts';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

const FUNCTION_TIMEOUT = 30000; // 30 seconds per function

async function invokeFunctionWithTimeout(url: string, headers: Record<string, string>, timeout: number) {
  const controller = new AbortController();
  const timeoutId = setTimeout(() => controller.abort(), timeout);

  try {
    console.log(`Invoking function: ${url}`);
    const response = await fetch(url, {
      method: 'POST',
      headers,
      signal: controller.signal,
    });
    clearTimeout(timeoutId);

    console.log(`Function response status: ${response.status}`);

    // Try to parse response body
    let responseData;
    try {
      const text = await response.text();
      console.log(`Function response body: ${text.substring(0, 500)}`);
      responseData = JSON.parse(text);
    } catch (e) {
      console.error('Failed to parse response:', e);
      responseData = { success: false, error: 'Failed to parse response' };
    }

    return { status: response.status, data: responseData };
  } catch (error) {
    clearTimeout(timeoutId);
    if (error instanceof Error && error.name === 'AbortError') {
      console.error(`Function timeout after ${timeout}ms`);
      throw new Error(`Function timeout after ${timeout}ms`);
    }
    console.error('Function invocation error:', error);
    throw error;
  }
}

serve(async (req) => {
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    console.log('=== UPDATE ALL SURF DATA STARTED ===');
    console.log('Timestamp:', new Date().toISOString());
    console.log('Location: Folly Beach, SC');

    const supabaseUrl = Deno.env.get('SUPABASE_URL');
    const supabaseAnonKey = Deno.env.get('SUPABASE_ANON_KEY');

    if (!supabaseUrl || !supabaseAnonKey) {
      const error = 'Missing Supabase environment variables';
      console.error(error);
      return new Response(
        JSON.stringify({
          success: false,
          error,
          timestamp: new Date().toISOString(),
        }),
        {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
          status: 500,
        }
      );
    }

    console.log('Supabase URL:', supabaseUrl);

    const results = {
      weather: null as any,
      tide: null as any,
      surf: null as any,
      report: null as any,
    };

    const errors = [];
    const requestHeaders = {
      'Authorization': `Bearer ${supabaseAnonKey}`,
      'Content-Type': 'application/json',
    };

    // Step 1: Fetch weather data
    console.log('Step 1: Fetching weather data for Folly Beach, SC...');
    try {
      const weatherResult = await invokeFunctionWithTimeout(
        `${supabaseUrl}/functions/v1/fetch-weather-data`,
        requestHeaders,
        FUNCTION_TIMEOUT
      );

      results.weather = weatherResult.data;

      if (weatherResult.status !== 200 || !weatherResult.data.success) {
        const errorMsg = weatherResult.data.error || `HTTP ${weatherResult.status}`;
        errors.push(`Weather: ${errorMsg}`);
        console.error('Weather fetch failed:', errorMsg);
      } else {
        console.log('Weather data fetched successfully');
      }
    } catch (error) {
      const errorMsg = error instanceof Error ? error.message : 'Unknown error';
      errors.push(`Weather: ${errorMsg}`);
      console.error('Weather fetch error:', error);
      results.weather = { success: false, error: errorMsg };
    }

    // Step 2: Fetch tide data
    console.log('Step 2: Fetching tide data for Folly Beach, SC...');
    try {
      const tideResult = await invokeFunctionWithTimeout(
        `${supabaseUrl}/functions/v1/fetch-tide-data`,
        requestHeaders,
        FUNCTION_TIMEOUT
      );

      results.tide = tideResult.data;

      if (tideResult.status !== 200 || !tideResult.data.success) {
        const errorMsg = tideResult.data.error || `HTTP ${tideResult.status}`;
        errors.push(`Tide: ${errorMsg}`);
        console.error('Tide fetch failed:', errorMsg);
      } else {
        console.log('Tide data fetched successfully');
      }
    } catch (error) {
      const errorMsg = error instanceof Error ? error.message : 'Unknown error';
      errors.push(`Tide: ${errorMsg}`);
      console.error('Tide fetch error:', error);
      results.tide = { success: false, error: errorMsg };
    }

    // Step 3: Fetch surf conditions
    console.log('Step 3: Fetching surf conditions for Folly Beach, SC...');
    try {
      const surfResult = await invokeFunctionWithTimeout(
        `${supabaseUrl}/functions/v1/fetch-surf-reports`,
        requestHeaders,
        FUNCTION_TIMEOUT
      );

      results.surf = surfResult.data;

      if (surfResult.status !== 200 || !surfResult.data.success) {
        const errorMsg = surfResult.data.error || `HTTP ${surfResult.status}`;
        errors.push(`Surf: ${errorMsg}`);
        console.error('Surf fetch failed:', errorMsg);
      } else {
        console.log('Surf conditions fetched successfully');
      }
    } catch (error) {
      const errorMsg = error instanceof Error ? error.message : 'Unknown error';
      errors.push(`Surf: ${errorMsg}`);
      console.error('Surf fetch error:', error);
      results.surf = { success: false, error: errorMsg };
    }

    // Step 4: Generate daily report (only if we have the required data)
    if (results.weather?.success && results.surf?.success) {
      console.log('Step 4: Generating daily report for Folly Beach, SC...');
      try {
        const reportResult = await invokeFunctionWithTimeout(
          `${supabaseUrl}/functions/v1/generate-daily-report`,
          requestHeaders,
          FUNCTION_TIMEOUT
        );

        results.report = reportResult.data;

        if (reportResult.status !== 200 || !reportResult.data.success) {
          const errorMsg = reportResult.data.error || `HTTP ${reportResult.status}`;
          errors.push(`Report: ${errorMsg}`);
          console.error('Report generation failed:', errorMsg);
        } else {
          console.log('Daily report generated successfully');
        }
      } catch (error) {
        const errorMsg = error instanceof Error ? error.message : 'Unknown error';
        errors.push(`Report: ${errorMsg}`);
        console.error('Report generation error:', error);
        results.report = { success: false, error: errorMsg };
      }
    } else {
      const msg = 'Skipping report generation - missing required data (weather or surf)';
      errors.push(msg);
      console.log(msg);
      results.report = { success: false, error: msg };
    }

    console.log('=== UPDATE ALL SURF DATA COMPLETED ===');
    console.log('Results summary:', {
      weather: results.weather?.success ? 'SUCCESS' : 'FAILED',
      tide: results.tide?.success ? 'SUCCESS' : 'FAILED',
      surf: results.surf?.success ? 'SUCCESS' : 'FAILED',
      report: results.report?.success ? 'SUCCESS' : 'FAILED',
    });
    console.log('Errors:', errors);

    // Consider it a success if at least weather and surf data were fetched
    // (tide is optional, report depends on weather and surf)
    const criticalSuccess = results.weather?.success && results.surf?.success;
    const allSuccess = errors.length === 0;

    return new Response(
      JSON.stringify({
        success: criticalSuccess, // Return success if critical data was fetched
        message: allSuccess 
          ? 'All surf data updated successfully for Folly Beach, SC' 
          : criticalSuccess
          ? 'Critical surf data updated successfully for Folly Beach, SC (some optional updates failed)'
          : 'Failed to update critical surf data for Folly Beach, SC',
        location: 'Folly Beach, SC',
        results,
        errors: errors.length > 0 ? errors : undefined,
        timestamp: new Date().toISOString(),
      }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 200, // Always return 200 to prevent client-side errors
      }
    );
  } catch (error) {
    console.error('=== UPDATE ALL SURF DATA FAILED ===');
    console.error('Error in update-all-surf-data:', error);
    return new Response(
      JSON.stringify({
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
        stack: error instanceof Error ? error.stack : undefined,
        timestamp: new Date().toISOString(),
      }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 200, // Return 200 to prevent client-side errors
      }
    );
  }
});

