    ALTER TABLE public.weather_forecast ADD COLUMN temperature_unit text;
  END IF;

  IF NOT EXISTS (SELECT 1 FROM information_schema.columns WHERE table_name = 'weather_forecast' AND column_name = 'short_forecast') THEN
    ALTER TABLE public.weather_forecast ADD COLUMN short_forecast text;
