  IF NOT EXISTS (SELECT 1 FROM information_schema.columns WHERE table_name = 'weather_forecast' AND column_name = 'is_daytime') THEN
    ALTER TABLE public.weather_forecast ADD COLUMN is_daytime boolean;
  END IF;
END $$;

-- Add missing columns to tide_data if they don't exist
DO $$ 
BEGIN
  IF NOT EXISTS (SELECT 1 FROM information_schema.columns WHERE table_name = 'tide_data' AND column_name = 'height_unit') THEN
    ALTER TABLE public.tide_data ADD COLUMN height_unit text;
  END IF;
END $$;

-- Update the unique constraint on weather_forecast to match what the edge function expects
ALTER TABLE public.weather_forecast
DROP CONSTRAINT IF EXISTS weather_forecast_date_key;

-- Create a composite unique constraint on date and period_name
DO $$
BEGIN
  IF NOT EXISTS (
    SELECT 1 FROM pg_constraint 
    WHERE conname = 'weather_forecast_date_period_name_key'
  ) THEN
    ALTER TABLE public.weather_forecast
    ADD CONSTRAINT weather_forecast_date_period_name_key UNIQUE (date, period_name);
  END IF;
END $$;

